public class Assignment2_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Write a for loop that prints 50 through 1 separated by spaces in the same line
		
		forLoop();
	}
public static void forLoop() {
	
	int a;
	for (a = 1; a <= 50; a++){
		System.out.print(a + " ");	
	}
}
}
